class Comp(object):
    """ subclass override """

    def __init__(self, val):
        self.val = val

    def __add__(self, other):   # x+2
        if isinstance(other, Comp):
            other = other.val
        return Comp(self.val + other)

    def __radd__(self, other):  # 2+x
        return Comp(other + self.val)

    def __iadd__(self,other):   # x+=2
        self.val+=other
        return self

    def __repr__(self):
        return f'Comp={self.val}'

    def someMethod(self):
        return NotImplemented


comp1, comp2 = Comp(88), Comp(99)
print(1+comp1)
print(comp1+comp2)
comp1+=3
print(comp1)

