class Cell(object):
    def __init__(self):
        self._alive = False
    @property
    def alive(self):
        return self._alive
    def set_state(self, state):
        self._alive = bool(state)
    set_alive = partialmethod(set_state, True) # new alias for method set_state
    set_dead = partialmethod(set_state, False)

c = Cell()
c.set_alive()

class Negator:

    @singledispatchmethod  # !! будет смотреть только на тип ТОЛЬКО ПЕРВОГО аргумента "arg"
    def neg(self, arg):
        raise NotImplementedError("Cannot negate a")

    @neg.register
    def _(self, arg: int): # вызовется при o.neg(1)
        return -arg

    @neg.register
    def _(self, arg: bool): # вызовется при o.neg(True)
        return not arg

import enum
@enum.unique
class Color(enum.Enum):
    RED = enum.auto()
    GREEN = enum.auto()
    BLUE = enum.auto()

print(Color.RED)
print(Color.RED is Color.RED) # сравнение is(по идентичности)
for i in Direction:
    print(i)

@enum.unique
class Direction(enum.IntFlag):
    UP = 1
    RIGHT = 2
    DOWN = 3
    LEFT = 4

print(Color.RED)
print( Direction.UP==1) #сравнение ==(по значению) так как enum это число
for i in Direction:
    print(i)
