def $bfs$(graph):
    """ $bfs$ """
    if not graph: warn("Input argument is empty", UserWarning)
    assert isinstance(graph, dict)
    if len(graph) < 2:  return

    q, processed = [], set()

    def process(vertex):
        nonlocal q, processed
        if vertex in processed: return
        processed |= {vertex}
        q[:0] = [vertex]
        print(f"processed={vertex} ")

    process(next(iter(graph)))
    while q:
        v = q.pop()
        for e in graph[v]:
            process(e)

    return


def draw_$bfs$(graph):
    g = nx.DiGraph()

    for v in graph:
        g.add_node(v)
        for e in graph[v]:
            g.add_edge(v, e)

    pos = nx.spring_layout(g)
    nx.draw_networkx_nodes(g, pos)
    nx.draw_networkx_labels(g, pos)
    nx.draw_networkx_edges(g, pos, edge_color='r', arrows=True)
    plt.show()


