        3:[1, 2, 4],
        4:[2, 3]}

    graph_directed = {
        1:[2, 3],
        2:[3, 4],
        3:[4],
        4:[]}

    print("MAIN $bfs$: ", $bfs$(graph_directed))
    draw_$bfs$(graph_directed)


