from abc import ABCMeta, abstractmethod

class MyAbstract(metaclass=ABCMeta):
    @abstractmethod
    def method1(self):...

class MyImplementation:
    def method1(self):
        print("mymethod")

o12=MyImplementation()
o12.method1()

class Comp:
    def __init__(self, val):
        self.val = val

    def __add__(self, other):   # x+2
        if isinstance(other, Comp):
            other = other.val
        return Comp(self.val + other)

    def __radd__(self, other):  # 2+x
        return Comp(other + self.val)

    def __iadd__(self,other):   # x+=2
        self.val+=other
        return self

    def __repr__(self):
        return f'Comp={self.val}'

c1, c2 = Comp(88), Comp(99)
print(1+c1)
print(c1+c2)
c1+=3
print(c1)

