class Entry():
    def __init__(self, key, value):
        self.key = key
        self.value = value

    def __lt__(self, other):
        return self.value < other.value

    def __repr__(self):
        return f"{self.key}-{self.value}"

heap, heap_dict = [], {}

def push(k, v):
    entry = Entry(v, k)  # ordering element goes first
    heappush(heap, entry)
    heap_dict[k] = entry

def pop(): return heappop(heap)

push(5, 10)
push(6, 9)
print("heap=", heap)
print("heap=", pop())

class Heap():
    """ class heap
        new-call pattern
        from heapq import *
    """
    REMOVED = '<removed-task>'  # placeholder for a removed task

    def __init__(self,list_):
        self.pq = list_ or []  # list of entries arranged in a heap
        self.entry_finder = { e[1]:e for e in self.pq}  # mapping of VALUES to entries
        heapify(self.pq)

    def push(self, key, value):
        'Add a new VALUE or update the priority of an existing task'
        if value in self.entry_finder:
            self.remove(value)
        entry = [key, value]
        self.entry_finder[value] = entry
        heappush(self.pq, entry)

    def remove(self, value):
        'Mark an existing VALUE as REMOVED.  Raise KeyError if not found.'
        entry = self.entry_finder.pop(value)
        entry[-1] = Heap.REMOVED

    def entry(self, value):
        return self.entry_finder[value]

    def peek(self):
        return self.pq[0]

    def pop(self):
        'Remove and return the lowest priority VALUE. Raise KeyError if empty.'
        while self.pq:
            priority, value = heappop(self.pq)
            if value is not Heap.REMOVED:
                del self.entry_finder[value]  # так как это операция pop то удалим также из словаря
                return value
        return None

    def __repr__(self):
        return f"heap={self.pq} \n dict={self.entry_finder}"

from dataclasses import dataclass, field

@dataclass(order=True)
class Entry():
    key: int
    value: Any = field(compare=False)

heap, heap_dict = [], {}

def push(k, v):
    entry = Entry(k, v)  # ordering element goes first
    heappush(heap, entry)
    heap_dict[k] = entry

def pop(): return heappop(heap)

push(5, 10)
push(6, 9)
print("heap=", heap)
print("heap=", pop())
